const NavLinks = styled.a`
  color: #1b1b18;
  font-size: 16px;
  font-style: normal;
  font-weight: 700;
  line-height: normal;
`;

const AuthButton = styled.button`
  border: none;
  outline: none;
  border-radius: 50rem;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 10px;
  font-size: 0.75rem;
  padding: 0.5rem 1.25rem;
  color: #1b1b18;
  font-weight: 600;
  border: 1px solid #e3e3e0;
  background: #f3f3f2;

  &.sign-in {
    color: #fff;
    background: #1b1b18;
  }

  &:active,
  &:focus {
    border: none;
    outline: none;
    border: 1px solid #e3e3e0;
  }
`;

export default function Navbar() {
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const Nav = styled.nav`
    height: 5.75rem;
    padding: 24px 120px;

    @media (width <= 1200px) {
      padding: 24px 60px;
    }

    @media (width <= 990px) {
      padding: 24px;
    }
  `;

  const LinksDiv = styled.div`
    display: flex;
    gap: 2rem;

    @media (width <= 1180px) {
      gap: 1rem;
    }

    @media (width <= 800px) {
      display: none;
    }
  `;

  const MobileLinks = styled.div`
    display: none;

    @media (width <= 800px) {
      display: block !important;
    }
  `;

  return (
    <Nav className="w-100 d-flex justify-content-between align-items-center">
      <a style={{ cursor: "pointer" }} href="/">
        <Logo />
      </a>
      <LinksDiv className="align-items-center">
        <NavLinks href="#">Components</NavLinks>
        <NavLinks href="#">Projects</NavLinks>
        <NavLinks href="#">Resources</NavLinks>
        <NavLinks href="#">Community</NavLinks>
        <NavLinks href="#">About</NavLinks>
        <div className="d-flex align-items center gap-md-1 gap-lg-3">
          {/* <AuthButton>Sign in</AuthButton> */}
          <AuthButton className="sign-in">Sign in</AuthButton>
        </div>
      </LinksDiv>
      <MobileLinks>
        <div className="d-flex gap-3">
          <AuthButton>Create Account</AuthButton>
          <Button
            style={{ width: "32px", height: "32px" }}
            className="rounded-circle p-0"
            variant="dark"
            onClick={handleShow}
          >
            <i className="bi bi-list"></i>
          </Button>
        </div>

        <Offcanvas show={show} onHide={handleClose} placement="end">
          <Offcanvas.Header closeButton>
            <Offcanvas.Title>DiscoverbOS</Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body>
            <div className="d-flex flex-column gap-3">
              <NavLinks href="#">Components</NavLinks>
              <NavLinks href="#">Projects</NavLinks>
              <NavLinks href="#">Resources</NavLinks>
              <NavLinks href="#">Community</NavLinks>
              <NavLinks href="#">About</NavLinks>
              <div className="d-flex align-items center gap-2 gap-md-1 gap-lg-3">
                <AuthButton>Sign in</AuthButton>
                <AuthButton className="sign-in">Create Account</AuthButton>
              </div>
            </div>
          </Offcanvas.Body>
        </Offcanvas>
      </MobileLinks>
    </Nav>
  );
}
